import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-article',
  templateUrl: './add-article.component.html',
  styleUrls: ['./add-article.component.css']
})
export class AddArticleComponent  {

  articleForm: FormGroup;
  selectedFile: File | null = null;

  constructor(private fb: FormBuilder, private http: HttpClient) {
    this.articleForm = this.fb.group({
      title: [''],
      author: [''],
      content: [''],
      industry:[''],
      articleImage: ['']
    });
  }
  
  onSubmit() {
    // Handle form submission
    const formData = new FormData();
    formData.append('title', this.articleForm.value.title);
    console.log(this.articleForm.value.title);
    
    formData.append('author', this.articleForm.value.author);
    formData.append('content', this.articleForm.value.content);    
    formData.append('industry', this.articleForm.value.industry);    
    if (this.selectedFile) {
      formData.append('articleImage', this.selectedFile, this.selectedFile.name);
    }

    const keys = Object.keys(formData)
    keys.forEach(key=>{
      console.log(key);
      
    });


    // Send formData to your server using HttpClient
    this.http.post('http://localhost:3000/api/v1/article/upload', formData).subscribe(
    response => {
      console.log('Server response:', response);
      // Reset the form after successful submission
      this.articleForm.reset();
      this.selectedFile = null;
    },
    error => {
      console.error('Error:', error);
    }
  );
  }

  onFileSelected(event: any) {
    // Handle file selection
    this.selectedFile = event.target.files[0];
  }

}
<div class="container mt-3 col-5">
  <form [formGroup]="articleForm" (ngSubmit)="onSubmit()" enctype="multipart/form-data">
    <div class="mb-3">
      <label for="title" class="form-label">Title</label>
      <input type="text" class="form-control" id="title" formControlName="title" placeholder="Enter article title">
    </div>
    <div class="mb-3">
      <label for="author" class="form-label">Author</label>
      <input type="text" class="form-control" id="author" formControlName="author" placeholder="Enter Author Name">
    </div>
    <div class="mb-3">
      <label for="content" class="form-label">Content</label>
      <textarea class="form-control" id="content" formControlName="content" rows="3"></textarea>
    </div>
    <!-- <div class="mb-3">
      <label for="content" class="form-label">Industy</label>
      <input class="form-control" id="industry" formControlName="industry" rows="3">
    </div> -->

    <select class="form-select"  formControlName="industry" id="industry" aria-label="Default select example">
      <option selected>---Select Industry---</option>
      <option value="Service">Service</option>
      <option value="Financial">Financial</option>
      <option value="Manufacturing">Manufacturing</option>
    </select>


    <div class="mb-3">
      <label for="file" class="form-label">File</label>
      <input type="file" id="file" name="articleImage" (change)="onFileSelected($event)">
    </div>
    <div class="d-grid gap-2 col-3">
      <button class="btn btn-primary" type="submit">Submit</button>
    </div>
  </form>
</div>
